      'Authorization': 'Bearer cal_live_f88ed09076e112ddf77a72a360e79a88',
      'cal-api-version': '2024-08-13',
      'Content-Type': 'application/json'
    },
    body: {
      cancellationReason: reason
    },
    json: true
  });

  return JSON.stringify({
    success: true,
    message: 'Cita cancelada correctamente.',
    reason: reason
  });

} catch (error) {
  return JSON.stringify({
    success: false,
    error: `Error al cancelar: ${error.message}`
  });
}

// Handle query
let email, fullName, phone, service, startUtc, duration;

if (typeof query === 'object' && query !== null) {
  email = query.email;
  fullName = query.fullName;
  phone = query.phone;
  service = query.service;
  startUtc = query.startUtc;
  duration = query.duration;
} else {
  try {
    const input = JSON.parse(query);
    email = input.email;
    fullName = input.fullName;
    phone = input.phone;
    service = input.service;
    startUtc = input.startUtc;
    duration = input.duration;
  } catch (e) {
    return JSON.stringify({
      success: false,
      error: 'Formato inv√°lido.'
    });
  }
}

// Validate required fields
if (!email || !fullName || !phone || !service || !startUtc || !duration) {
  return JSON.stringify({
    success: false,
    error: 'Faltan datos. Necesito: email, fullName, phone, service, startUtc, duration.'
  });
}

// Extract the date from startUtc for double-booking check
const requestedDate = startUtc.split('T')[0];

// Check for existing bookings on the same day (DOUBLE-BOOKING PREVENTION)
const checkUrl = `https://api.cal.com/v2/bookings?eventTypeId=3502752&attendeeEmail=${encodeURIComponent(email)}&take=100&skip=0`;

let existingBookings;
