  existingBookings = await this.helpers.httpRequest({
    method: 'GET',
    url: checkUrl,
    headers: {
      'Authorization': 'Bearer cal_live_01266add88dbb76e38fe6637e3130a92',
      'cal-api-version': '2024-08-13'
    },
    json: true
  });
} catch (error) {
  // Continue anyway if check fails
  existingBookings = { data: [] };
}

const activeBookings = (existingBookings.data || []).filter(b => b.status === 'accepted');

// Check if any booking is on the same day
const sameDayBooking = activeBookings.find(b => {
  const bookingDate = b.start.split('T')[0];
  return bookingDate === requestedDate;
});

if (sameDayBooking) {
  const existingTime = new Date(sameDayBooking.start).toLocaleString('es-ES', {
    hour: '2-digit',
    minute: '2-digit',
    timeZone: 'Europe/Madrid'
  });
  const existingService = sameDayBooking.responses?.service || sameDayBooking.title || 'Cita';

  return JSON.stringify({
