    doubleBooking: true,
    message: `El paciente ya tiene una cita ese d√≠a: ${existingService} a las ${existingTime}. ¬øQuiere a√±adir otra cita o cambiar la existente?`,
    existingBooking: {
      service: existingService,
      time: existingTime
    }
  });
}

// No conflict - proceed to create booking
const createUrl = 'https://api.cal.com/v2/bookings';

try {
  const response = await this.helpers.httpRequest({
    method: 'POST',
    url: createUrl,
    headers: {
      'Authorization': 'Bearer cal_live_f88ed09076e112ddf77a72a360e79a88',
      'cal-api-version': '2024-08-13',
      'Content-Type': 'application/json'
    },
    body: {
      eventTypeId: 3502752,
      start: startUtc,
      attendee: {
        name: fullName,
        email: email,
        timeZone: 'Europe/Madrid',
        phoneNumber: phone
      },
      lengthInMinutes: parseInt(duration, 10),
      bookingFieldsResponses: {
        service: service
      }
    },
    json: true
  });

  return JSON.stringify({
    success: true,
    message: 'Cita creada correctamente.',
    data: response
  });

} catch (error) {
  return JSON.stringify({
    success: false,
    error: `Error al crear cita: ${error.message}`
  });
}

// Get the timestamp from "EVERYDAY AT 8AM" node and convert to UTC
const triggerData = $('EVERYDAY AT 8AM').first().json;
const triggerTimestamp = new Date(triggerData.timestamp);

// Calculate the 24h-40h window from the trigger time
const window24h = new Date(triggerTimestamp.getTime() + (24 * 60 * 60 * 1000));
const window40h = new Date(triggerTimestamp.getTime() + (40 * 60 * 60 * 1000));

// Get the bookings from the input
const inputData = $input.first().json;
const bookings = inputData.data || [];
const pagination = inputData.pagination || {};

// Filter bookings:
// 1. Status must be "accepted" (not cancelled)
// 2. Start time must be between 24h and 40h from trigger
const filteredBookings = bookings.filter(booking => {
  // Check status - only "accepted" can pass through
  if (booking.status !== 'accepted') {
    return false;
  }

  // Parse the booking start time (already in UTC)
  const bookingStart = new Date(booking.start);

  // Check if booking is within the 24h-40h window
  return bookingStart >= window24h && bookingStart <= window40h;
});

// Build output array
const output = [];

// First item: pagination and count (ALWAYS included)
output.push({
  json: {
    pagination: pagination,
    filteredCount: filteredBookings.length
