});

// If no bookings match, just return the first item with count
if (filteredBookings.length === 0) {
  return output;
}

// Following items: each booking without pagination
filteredBookings.forEach(booking => {
  const startDate = new Date(booking.start);

  // Format for display in Spanish (Madrid timezone)
  const dateOptions = {
    weekday: 'long',
    day: 'numeric',
    month: 'long',
    year: 'numeric',
    timeZone: 'Europe/Madrid'
  };

  const timeOptions = {
    hour: '2-digit',
    minute: '2-digit',
