  const service = booking.bookingFieldsResponses?.service || booking.title || 'Cita';

  output.push({
    json: {
      bookingId: booking.id,
      bookingUid: booking.uid,
      status: booking.status,
      patientName: attendee.name || 'Paciente',
      patientEmail: attendee.email || '',
      patientPhone: attendee.phoneNumber || '',
      service: service,
      date: formattedDate,
      time: formattedTime,
      fullDateTime: `${formattedDate} a las ${formattedTime}`,
      startUtc: booking.start,
      endUtc: booking.end,
      duration: booking.duration,
      whatsappNumber: attendee.phoneNumber?.replace(/\s+/g, '') || ''
    }
  });
});

return output;

// Get all bookings from "24H TRUE GUARDRAIL", skipping the first item (pagination)
