parameters.jsCode:
let input = JSON.stringify($input.first().json)
return {
  json: {
    input
  }
}

const jobs = $input.all().map(i=>i.json)
const seen = new Set();
const uniqueJobs = [];

for (const job of jobs) {
  if (!seen.has(job.job_id)) {
    seen.add(job.job_id);
    uniqueJobs.push({ json:job });
  }
}

return uniqueJobs

